package com.zero.customer.common.mybatis;import com.baomidou.mybatisplus.mapper.MetaObjectHandler;import org.apache.ibatis.reflection.MetaObject;import org.springframework.stereotype.Component;import java.util.Date;/** * All rights Reserved, Designed By Suixingpay. * * @author: 张得钧<zhang_dj @ suixingpay.com> * @date: 2019-11-24 14:52 * @Copyright: 2019 Suixingpay. All rights reserved. * 注意：本内容仅限于随行付支付有限公司内部传阅，禁止外泄以及用于其他的商业用途。 */@Componentpublic class MetaObjectHandlerConfig extends MetaObjectHandler {    @Override    public void insertFill(MetaObject metaObject) {        Date now = new Date();        setFieldValByName("createDate", now, metaObject);    }    @Override    public void updateFill(MetaObject metaObject) {        Date now = new Date();        this.setFieldValByName("modifyDate", now, metaObject);    }}