package com.zero.customer.common.utils.reflect.self;import com.zero.customer.common.utils.reflect.other.Man;import java.lang.reflect.Method;/** * All rights Reserved, Designed By Suixingpay. * * @author: 张得钧<zhang_dj @ suixingpay.com> * @date: 2019-10-29 18:01 * @Copyright: 2019 Suixingpay. All rights reserved. * 注意：本内容仅限于随行付支付有限公司内部传阅，禁止外泄以及用于其他的商业用途。 */public class MyInvocationHandlerImpl implements MyInvocationHandler {    private Man man;    public MyInvocationHandlerImpl(Man man){        this.man = man;    }    @Override    public Object invoke(Object proxy, Method method, String orgs) throws Throwable {        before();        method.invoke(man,orgs);        after();        return null;    }    public void before(){        System.out.println("一时单身一时爽，一直单身一直爽~~~");    }    public void after(){        System.out.println("对象，艾玛~真香！");    }}