package com.zero.customer.controller;import com.zero.customer.common.utils.spring.GainSpringContext;import com.zero.customer.config.property.GainPropertyData;import com.zero.customer.service.SelectService;import com.zero.customer.service.SelectUser;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * All rights Reserved, Designed By Suixingpay. * * @author: 张得钧<zhang_dj @ suixingpay.com> * @date: 2019-11-24 15:27 * @Copyright: 2019 Suixingpay. All rights reserved. * 注意：本内容仅限于随行付支付有限公司内部传阅，禁止外泄以及用于其他的商业用途。 */@RestController@RequestMapping("/login")public class LoginController {    @Autowired    SelectUser loginMapper;    @Autowired    SelectService serverMapper;    @GetMapping("/consumer")    public String dc() {        String key = GainPropertyData.getData("name");        System.out.println("获取配置====信息属性为:"+key);        System.out.println(serverMapper.getServer(1));        return loginMapper.getUser(1);    }    @RequestMapping("/springBean")    public void connectSpring(){        SelectService selectService = (SelectService)GainSpringContext.getBean("selectServiceImpl");        System.out.println(selectService.getServer(1));    }}